function NumberToText(inputNumber, suffix = false)
{
    var unitNumbers = ["kh√¥ng", "m·ªôt", "hai", "ba", "b·ªën", "nƒÉm", "s√°u", "b·∫£y", "t√°m", "ch√≠n" ];
    var placeValues = ["", "ngh√¨n", "tri·ªáu", "t·ª∑"];
    var isNegative = false;

    // -12345678.3445435 => "-12345678"
    var sNumber = inputNumber.toString();
    var number = parseFloat(sNumber);
    if (number < 0) {
        number = -number;
        sNumber = number.toString();
        isNegative = true;
    }


    var ones, tens, hundreds;
    var positionDigit = sNumber.length;   // last -> first
    var result = " ";


    if (positionDigit == 0)
        result = unitNumbers[0] + result;
    else {
        // 0:       ###
        // 1: ngh√¨n ###,###
        // 2: tri·ªáu ###,###,###
        // 3: t·ª∑    ###,###,###,###
        var placeValue = 0;

        while (positionDigit > 0) {
            // Check last 3 digits remain ### (hundreds tens ones)
            tens = hundreds = -1;
            ones = parseInt(sNumber.substr(positionDigit - 1, 1));
            positionDigit--;
            if (positionDigit > 0) {
                tens = parseInt(sNumber.substr(positionDigit - 1, 1));
                positionDigit--;
                if (positionDigit > 0) {
                    hundreds = parseInt(sNumber.substr(positionDigit - 1, 1));
                    positionDigit--;
                }
            }

            if ((ones > 0) || (tens > 0) || (hundreds > 0) || (placeValue == 3))
                result = placeValues[placeValue] + result;

            placeValue++;
            if (placeValue > 3) placeValue = 1;

            if ((ones == 1) && (tens > 1))
                result = "m·ªët " + result;
            else {
                if ((ones == 5) && (tens > 0))
                    result = "lƒÉm " + result;
                else if (ones > 0)
                    result = unitNumbers[ones] + " " + result;
            }
            if (tens < 0)
                break;
            else {
                if ((tens == 0) && (ones > 0)) result = "l·∫ª " + result;
                if (tens == 1) result = "m∆∞·ªùi " + result;
                if (tens > 1) result = unitNumbers[tens] + " m∆∞∆°i " + result;
            }
            if (hundreds < 0) break;
            else {
                if ((hundreds > 0) || (tens > 0) || (ones > 0))
                    result = unitNumbers[hundreds] + " trƒÉm " + result;
            }
            result = " " + result;
        }
    }
    result = result.trim();
    if (isNegative) result = "√Çm " + result;
    return result + (suffix ? " ƒë·ªìng ch·∫µn" : "");
}
